sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },
	//Function to check if date is past to current date
	isDatePast : function(strDate)
	{
		//create GlideDateTime objects for the current date and the test date
		var rightNow = new GlideDateTime();
		var testdate = new GlideDateTime(strDate);

		//if the test date is before current date, then return true, else return false
		if(testdate.before(rightNow))
			{
				return true;
			}
		else
			{
				return false;
			}
	},

	//function to check if selected date is today or not
	isDateToday : function(strDate)
	{
		//create glidedatetime for current datetime and for the test date time. we cannot reuse the objects of rightnow from ispast function. we need to create a new glidedatetime objects in this function.
		var rightnow = new GlideDateTime();
		var testdate = new GlideDateTime(strDate);

		//get only local date from rightnow and testdate objects
		var today = rightnow.getLocalDate();
		var istoday = testdate.getLocalDate();


		//compare today and istoday objects, return true if value is 0, else return false
		if(istoday.compareTo(today) == 0)
			{
				return true;
			}
		else
			{
				return false;
			}
	},

    type: 'NeedItUtils'
};
